CREATE USER 'MicroProAdmin'@'%' IDENTIFIED VIA mysql_native_password USING 'pro123';GRANT ALL PRIVILEGES ON *.* TO 'MicroProAdmin'@'%' REQUIRE NONE WITH GRANT OPTION MAX_QUERIES_PER_HOUR 0 MAX_CONNECTIONS_PER_HOUR 0 MAX_UPDATES_PER_HOUR 0 MAX_USER_CONNECTIONS 0;